  `bookTags` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `bookCover` mediumtext CHARACTER SET utf8,
  `fileName` mediumtext CHARACTER SET utf8
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;


CREATE TABLE `book` (
  `bookTitle` varchar(255) CHARACTER SET utf8 NOT NULL,
  `bookIsbn` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
